package ch_02_ex02;

public class ConcatenationExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1 = "num = " + 3 + 4;
		String str2 = 3 + 4 + " = num";
		System.out.println("str1 -> " + str1);
		System.out.println("str2 -> " + str2);
		

	}

}
