public class ConditionalOperationExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 20, b = 30, max;
		max = (a > b) ? a : b;
		
		/*
		if(a>b) {
			max = a;
		} else {
			max = b;
		}
		*/
		
		System.out.println("max -> " + max);
	}

}
