package ch_04_ex01;

public class For_05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 0 ; i <=3 ; i++) {
			for(int j = 0 ; j <= i ; j++) {
				System.out.print("*");
			}
			System.out.println();
		}
		for(int i = 2 ; i >= 0 ; i--) {
			for(int j = 0 ; j <=i ; j++) {
				System.out.print("*");
			}
			System.out.println();
		}
		
		for(int i = 1 ; i <=3 ; i++) {
			for(int j = 0 ; j <= i ; j++) {
				System.out.print("*");
			}
			System.out.println();
		}
		for(int i = 2 ; i >= 0 ; i--) {
			for(int j = 0 ; j <=i ; j++) {
				System.out.print("*");
			}
			System.out.println();
		}
	}
}
