package ch_04_ex01;

public class If_01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = -12;
		if(a > 0) {
			System.out.println(a + " is Positive.");
		} else {
			System.out.println(a + " is Negative.");
		}
		
		System.out.println("The Program is ended");
	}

}
