package ch_02_ex03;

public class Variable01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		float f1, f2;
		double d1;
		
		f1 = 10.1F;
		System.out.printf("f1 = %.1f \n", f1);
		
		f2 = 10.123F;
		System.out.printf("f2 = %.1f \n", f2);
		System.out.printf("f2 = %.2f \n", f2);
		System.out.printf("f2 = %.3f \n", f2);
		
		d1 = 100.4460854788;
		System.out.printf("d1 = %f \n", d1);
		System.out.printf("d1 = %.2f \n", d1);
		System.out.printf("d1 = %.3f \n", d1);
		System.out.printf("d1 = %.4f \n", d1);
		System.out.printf("d1 = %.5f \n", d1);
		System.out.printf("d1 = %.6f \n", d1);
		System.out.printf("d1 = %.7f \n", d1);
		System.out.printf("d1 = %.8f \n", d1);
		System.out.printf("d1 = %.9f \n", d1);
		System.out.printf("d1 = %.10f \n", d1);

	}

}
